mapper.select:
select id, flow_name, config, enable, create_at, update_at
from flow
where id = #{flowId}
;

select id, flow_name, config, enable, create_at, update_at
from flow

    where flow_name like concat('%', #{flowName}, '%')

limit #{offset}, #{limit}
;

select count(1) from flow
where flow_name like concat('%', #{flowName}, '%')
;

mapper.update:
update flow
set enable = #{enable}
where id = #{flowId}
;
